import { Component, OnInit } from '@angular/core';
import { PostService } from 'app/@core/services/api/post.service';
import { Observable, Observer } from 'rxjs';
export interface Iunit {
  id: string;
  name: string;
  address: string;
  description: string;
  created_at: string;
  updated_at: string;

}

@Component({
  selector: 'app-unit',
  templateUrl: './unit.component.html',
  styleUrls: ['./unit.component.scss']
})
export class UnitComponent implements OnInit {
  listUnit: Iunit[] = [];
  constructor(private unit: PostService) {

  }

  ngOnInit(): void {
    this.getUnit();
    this.delete();
  }
  getUnit() {
    this.unit.getAllUnit().subscribe(res => {
      console.log(res);
      this.listUnit = res.data;
    }, error => {
      console.error(error);
    });
  }


  onDelete(data: Iunit, id: number) {
    this.unit.delete('https://knowledgehub.demopolyct.online/api/unit' + id)
      .subscribe(res => {
        console.log(res);
        this.listUnit = res.data
      }, error => {
        console.error(error);

      });




  }

}
